ersion":1},"userdoc":{"kind":"user","methods":{"cancelSettlementTask(address)":{"notice":"Cancels a settlement task for a given market."},"checkUpkeep(bytes,bytes)":{"notice":"method that is simulated by the keepers to see if any work actually needs to be performed. This method does does not actually need to be executable, and since it is only ever simulated it can consume lots of gas."},"createSettlementTask(address)":{"notice":"Creates a settlement task for a given market."},"performUpkeep(bytes)":{"notice":"method that is actually executed by the keepers, via the registry. The data returned by the checkUpkeep simulation will be passed into this method to actually be executed."},"resolveSettlement(address,bytes)":{"notice":"Resolves the settlement of a market."},"settle(address)":{"notice":"Settles a market."},"updatePrice(address,bytes)":{"notice":"Updates the price using off-chain data."},"withdraw(address,uint256)":{"notice":"Withdraws a specified amount of funds from the contract to a recipient address."}},"version":1}},"settings":{"compilationTarget":{"contracts/core/automation/Mate2MarketSettlement.sol":"Mate2MarketSettlement"},"evmVersion":"paris","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":30000},"remappings":[":@chromatic-protocol/contracts/=contracts/",":ds-test/=lib/forge-std/lib/ds-test/src/",":forge-std/=lib/forge-std/src/"]},"sources":{"@openzeppelin/contracts/interfaces/IERC1155.sol":{"content":"// SPDX-License-Identifier: MIT
